from time import sleepfrom urllib import requestfrom django.views import genericfrom django.contrib import messagesfrom django.shortcuts import renderfrom django import urlsfrom django.contrib.auth import viewsfrom django.shortcuts import redirectfrom django.contrib.messages.views import SuccessMessageMixinfrom authentication import formsclass LoginView(views.LoginView):    form_class = forms.LoginForm    template_name = 'login.html'    success_url = urls.reverse_lazy('note-list')    def dispatch(self, request, *args, **kwargs):        if request.user.is_authenticated:            return redirect('/notes/')        return super().dispatch(request, *args, **kwargs)class LogoutView(views.LogoutView):    next_page = urls.reverse_lazy('login')class UserCreate(generic.FormView):    form_class = forms.CreateUserForm    success_url = urls.reverse_lazy('login')    template_name = 'signup.html'    def form_valid(self, form):        form.save()        return super(UserCreate, self).form_valid(form)    def form_invalid(self, form):        return super(UserCreate, self).form_invalid(form)